import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
	content: {
		flex: 1,
		// flexDirection: 'row',
		// alignItems: 'center',
		// justifyContent: 'center'
	},
	container: {
		flex: 1,
		alignItems: 'flex-start',
		paddingHorizontal: 20
	},
	top: {
		height: 200,
		width: "100%",
		backgroundColor: '#2c8ef4',
		alignItems: 'center',
		justifyContent: 'center',
	}
});

export default styles;
