import {
    GET_PRODUCTS,
    GET_PRODUCT,
    CLEAR_PRODUCT,
    PRODUCT_ERROR,
    PRODUCT_LOADING
} from '../actions/types';
const initialState = {
    product: {
        photosArray: []
    },
    products: [],
    loading: false,
    error: {}
};

function productReducer(state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case PRODUCT_LOADING:
            return {
                ...state,
                loading:true
            }
        case GET_PRODUCTS:
            return {
                ...state,
                products: payload,
                loading: false
            };
        case GET_PRODUCT:
            return {
                ...state,
                product: payload,
                loading: false
            }
        case CLEAR_PRODUCT:
            return {
                ...state,
                product: {
                    photosArray: []
                }
              };
        default:
            return state;
    }
}

export default productReducer;
