import React from 'react';
import {
	FlatList,
	Text,
	View,
	Image,
	TouchableHighlight
} from 'react-native';
import styles from './styles';
import { categories } from '../../data/dataArrays';
import { getNumberOfSubCategories } from '../../data/MockDataAPI';
import MenuImage from '../../components/MenuImage/MenuImage';
import TreeView from 'react-native-final-tree-view';
import { ScrollView } from 'react-native-gesture-handler';

export default class HomeScreen extends React.Component {
	static navigationOptions = ({ navigation }) => ({

		title: 'Home',
		headerLeft: () => <MenuImage
			onPress={() => {
				navigation.openDrawer();
			}}
		/>
	});


	constructor(props) {
		super(props);
		this.state = {
			categories: [{}]
		}
	}

	componentDidMount() {
		this.props.getCategories(this.props.token);
		this.setState({ categories: this.props.categories }, () => {
		});
	}

	onPressCategory = (item, haschild) => {
		if (!haschild) {
			const title = item.name;
			const category = item;
			this.props.setCategory({ category });
			this.props.navigation.navigate('ProductList');
		}
	};

	renderCategory = ({ item }) => (
		<TouchableHighlight underlayColor='rgba(73,182,77,0.9)' onPress={() => this.onPressCategory(item)}>
			<View style={styles.categoriesItemContainer}>
				<Image style={styles.categoriesPhoto} source={item.photo_url} />
				<Text style={styles.categoriesName}>{item.name}</Text>
				<Text style={styles.categoriesInfo}>{getNumberOfSubCategories(item.id)} recipes</Text>
			</View>
		</TouchableHighlight>
	);
	getIndicator = (isExpanded, hasChildrenNodes) => {
		if (!hasChildrenNodes) {
			return (<Image style={{ width: 30, height: 30, marginRight: 20 }} source={require('../../../assets/icons/category.png')} />);
		} else if (isExpanded) {
			return (<Image style={{ width: 30, height: 30, marginRight: 20 }} source={require('../../../assets/icons/down.png')} />);
		} else {
			return (<Image style={{ width: 30, height: 30, marginRight: 50 }} source={require('../../../assets/icons/right.png')} />);
		}
	};

	render() {

		return (
			<ScrollView >
				<View style={styles.container}>
					<TreeView

						data={this.props.categories}
						getCollapsedNodeHeight={() => 50}
						renderNode={
							({
								node,
								level,
								isExpanded,
								hasChildrenNodes
							}) => {
								return (
									<View style={styles.itemStyle}>
										{!hasChildrenNodes ? (
											<TouchableHighlight underlayColor='rgba(73,182,77,0.9)' onPress={() => this.onPressCategory(node, hasChildrenNodes)}>
												<Text style={{ marginLeft: level * 20 + 20, fontSize: 30 }}>
													{this.getIndicator(isExpanded, hasChildrenNodes)}
													{"    " + node.name}
												</Text>
											</TouchableHighlight>)
											: (
												<Text style={{ marginLeft: level * 20 + 20, fontSize: 30 }}>
													{this.getIndicator(isExpanded, hasChildrenNodes)}
													{"    " + node.name}
												</Text>)}
									</View>
								);
							}}
					/>
				</View>
			</ScrollView>
		);
	}
}
