import {
    GET_CATEGORIES,
    CLEAR_CATEGORY,
    SET_CATEGORY
} from '../actions/types';
const initialState = {
    category: null,
    categories: [],
    loading: true,
    error: {}
};

function categoryReducer(state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case GET_CATEGORIES:
            return {
                ...state,
                categories: payload,
                loading: false
            };
        case SET_CATEGORY:
            return {
                ...state,
                category: payload,
                loading: false
            }
        case CLEAR_CATEGORY:
            return {
                ...state,
                category: null
              };
        default:
            return state;
    }
}

export default categoryReducer;
